import React from "react";
import axios from "axios";
import Layout from "../../../components/Layout";
import { CardGroup, Card } from "semantic-ui-react";
// // import Patient from "../../Ethereum/patient";
// import truncateMiddle from "truncate-middle";
// // import header from "../../components/header";
// import { Card, Table, Grid, Button, Popup } from "semantic-ui-react";
// // import web3 from "../../Ethereum/web3";
// import { Link } from "../../../routes";
// import { response } from "express";


class dataRead extends React.Component {

    static async getInitialProps(props) {
        const { referenceKey } = props.query;

        function getData(key) {

            return axios.get(`http://localhost:5000/references/get/${key}`)
                .then(res => {
                    const item = res;
                    return item.data;
                }).catch(error => console.log(error));

        }
        const refData = await getData(referenceKey)
        return { referenceKey, refData }

    }

    renderData() {
        const {
            referenceKey,

        } = this.props

        return (
            <Card.Content
                header={referenceKey}
                meta='Friend'
                description='Elliot is a music producer living in Chicago.'
            />
        )

    }


    render() {
        return (
            <Layout>
                <CardGroup>
                    <Card>
                        {this.renderData()}
                    </Card>
                </CardGroup>
            </Layout>
        );
    }
}
export default dataRead;
