import React from 'react';
import axios from 'axios';
import Patient from '../../../Ethereum/patient';
import CryptoJs from 'crypto-js';
import Layout from "../../../components/Layout";
import { Button, Input, Form, Message } from "semantic-ui-react";
import web3 from "../../../Ethereum/web3";
import { Router } from "../../../routes";

class addRecord extends Reac.Component {
    static async getInitialProps(props) {
        const { address } = props.query
        return { address }
    }

    state = {
        data: '',
        dataHash: '',
        dataOwner: '',
        date: ''
    }
    onSubmit = async event => {
        event.preventDefault();
        // hash the data
        const refer = this.state.data;
        const hashedRefer = CryptoJs.SHA256(refer);
        const item =
        {
            data: refer,
            dataHash: hashedRefer.toString(CryptoJs.enc.Hex),
            dataOwner: this.state.dataOwner,
            date: this.state.date
        }
        const patient = Patient.getContract(item.dataOwner);

        try {
            const accounts = await web3.eth.getAccounts();
            await patient.methods.StoreReferences(
                item.dataHash,
                item.date).send({
                    from: accounts[0],
                });
        } catch (err) {
            console.log({ errormessage: err.message });
        }
        // send to private server
        axios.post('http://localhost:5000/references/save', item)
            .then(res => console.log(res.data));

        console.log(item);

        this.setState({
            data: '',
            dataHash: '',
            dataOwner: '',
            date: ''
        })
    }
    render() {
        return (
            <Layout>
                <div>
                    <Form onSubmit={this.onSubmit}>
                        <Form.Field>
                            <Input
                                label="Input Data"
                                value={this.state.data}
                                onChange={event => this.setState({ data: event.target.value })} />

                            <Input
                                label="Data Owner"
                                value={this.dataOwner}
                                onChange={event => this.setState({ dataOwner: event.target.value })} />

                            <Input
                                label="Date "
                                value={this.state.date}
                                onChange={event => this.setState({ date: event.target.value })} />
                        </Form.Field>
                        <Button className="btn btn-primary">Submit</Button>
                    </Form>
                </div>
            </Layout>
        );
    }
}
export default addRecord;
