import React from "react";
import axios from "axios";
import Layout from "../../../components/Layout";
import { CardGroup, Card } from "semantic-ui-react";
import truncateMiddle from "truncate-middle";
import Router from 'next/router';



class dataRead extends React.Component {

    static async getInitialProps(props) {
        const { referenceKey } = props.query;
        function getData(key) {
            return axios.get(`http://localhost:5000/info/get/${key}`, {
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `bearer ${localStorage.getItem('19divoc')}`
                }
            })
                .then(res => {
                    const item = res;
                    return item.data;
                }).catch((error) => {
                    const errStatus = error.response.status;
                    if (errStatus === 403) {
                        Router.push('/login');
                    } else {
                        Console.log('WTF')
                    }
                });
        }
        const refData = await getData(referenceKey)
        return { referenceKey, refData }

    }

    renderData() {

        const {
            referenceKey,
            refData
        } = this.props

        return (refData);

        // return (
        //     <Card.Content
        //         header={truncateMiddle(refData.dataHash, 5, 5, '.....')}
        //         meta={refData.createdAt}
        //         description={refData.data}
        //     />
        // )

    }


    render() {
        return (
            <div>
                {this.renderData()}</div>
            // <Layout>
            //     <CardGroup>
            //         <Card>
            //             {this.renderData()}
            //         </Card>
            //     </CardGroup>
            // </Layout>
        );
    }
}
export default dataRead;
