import React from "react";
import Layout from "../../components/Layout";
import Patient from "../../Ethereum/patient";
// import header from "../../components/header";
import { Card, Table, Grid, Button } from "semantic-ui-react";
import web3 from "../../Ethereum/web3";
import { Link } from "../../routes";

class patientShow extends React.Component {
    static async getInitialProps(props) {
        const { address } = props.query;
        const patient = Patient(address);


        const referencesCount = await patient.methods.GetReferences().call();
        const references = await Promise.all(
            Array(parseInt(referencesCount))
                .fill()
                .map((element, index) => {
                    return patient.methods.data_references(index).call();
                })
        );

        console.log(references);
        return {
            references
        };

    }
    renderCards() {

        return this.props.references.map((link, index) => {
            return (
                <Table.Row>
                    <Table.Cell>Jamie</Table.Cell>
                    <Table.Cell>Approved</Table.Cell>
                    <Table.Cell>Shorter description</Table.Cell>
                </Table.Row>
            )
        });

    }
    render() {
        return (
            <Layout>
                <Grid>
                    <Grid.Column width={10}>
                        <Grid.Row>
                            <Table>
                                <Table.Header>
                                    <Table.Row>
                                        <Table.HeaderCell>ID</Table.HeaderCell>
                                        <Table.HeaderCell>Description</Table.HeaderCell>
                                        <Table.HeaderCell>Amount</Table.HeaderCell>
                                        <Table.HeaderCell>Recipient</Table.HeaderCell>
                                        <Table.HeaderCell>Approval Count</Table.HeaderCell>
                                        <Table.HeaderCell>Approve</Table.HeaderCell>
                                        <Table.HeaderCell>Finalize</Table.HeaderCell>
                                    </Table.Row>
                                </Table.Header>

                                <Table.Body>{this.renderRow()}</Table.Body>
                            </Table>
                            {/* <Link route={`/patients/${this.props.address}/requests`}>
                                <a>
                                    <Button content="view Requests" className="ui green button" />
                                </a>
                            </Link> */}
                        </Grid.Row>
                    </Grid.Column>
                </Grid>
            </Layout>
        );
    }
}
export default patientShow;
