import React from "react";
import Layout from "../../components/Layout";
import patient from "../../Ethereum/patient";
// import header from "../../components/header";
import { Card, Grid, Button } from "semantic-ui-react";
import ContributeForm from "../../components/contributeForm";
import web3 from "../../Ethereum/web3";
import { Link } from "../../routes";

class patientShow extends React.Component {
    static async getInitialProps(props) {
        const patient = patient(props.query.address);

        const referencesCount = await patient.methods.GetReferences().call();
        const references = await Promise.all(
            Array(parseInt(referencesCount))
                .fill()
                .map((element, index) => {
                    return patient.methods.data_references(index).call();
                })
        );
        return { dataHash, TimeStamp }
    }
    renderCards() {
        const {
            dataReference,
            uploadTime,
        } = this.props;

        const items = [
            {
                header: dataReference,
                meta: uploadTime,
                description:
                    "to access the data click on the link at the top",
                style: { overflowWrap: "break-word" }
            },
        ];
        return <Card.Group items={items} />;
    }
    render() {
        return (
            <Layout>
                <Grid>
                    <Grid.Column width={10}>
                        <Grid.Row> {this.renderCards()} </Grid.Row>
                        <Grid.Row>
                            <Link route={`/patients/${this.props.address}/requests`}>
                                <a>
                                    <Button content="view Requests" className="ui green button" />
                                </a>
                            </Link>
                        </Grid.Row>
                    </Grid.Column>
                </Grid>
            </Layout>
        );
    }
}
export default patientShow;
