import React from 'react';
import axios from 'axios';
import Layout from "../../components/Layout";
import { Button, Input, Form, Message, Icon } from "semantic-ui-react";


class addRecord extends React.Component {

    state = {
        email: '',
        passworrd: '',
        loading: false,
    }

    onSubmit = async event => {

        event.preventDefault();
        // hash the data
        const { email, password } = this.state;
        const credentials = JSON.stringify({
            email: email,
            password: password
        })
        axios.post('http://localhost:5000/users/api/login', credentials, {
            headers: {
                accept: 'application/json',
                'accept-language': 'en_US',
                'content-type': 'application/x-www-form-urlencoded'
            }
        }).then(res => {
            console.log(res);
        }).catch(error => console.log(error));

    }
    render() {
        return (
            <Layout>
                <div>
                    <Form onSubmit={this.onSubmit}>
                        <Form.Field>
                            <Input
                                label="Email"
                                value={this.state.email}
                                onChange={event => this.setState({ email: event.target.value })} type="email" />

                            <Input
                                label="Password"
                                value={this.state.password}
                                onChange={event => this.setState({ password: event.target.value })}
                                type="password" />
                        </Form.Field>
                        <Button loading={this.state.loading} className="btn btn-primary">Submit</Button>
                    </Form>
                </div>
            </Layout >
        );
    }
}
export default addRecord;
