import React from "react";
import Layout from "../../components/Layout";
import Patient from "../../Ethereum/patient";
import truncateMiddle from "truncate-middle";
// import header from "../../components/header";
import { Card, Table, Grid, Button, Popup } from "semantic-ui-react";
import web3 from "../../Ethereum/web3";
import { Link } from "../../routes";

class patientShow extends React.Component {
    static async getInitialProps(props) {
        const { address } = props.query;
        const patient = Patient(address);


        const referencesCount = await patient.methods.GetReferences().call();
        const references = await Promise.all(
            Array(parseInt(referencesCount))
                .fill()
                .map((element, index) => {
                    return patient.methods.data_references(index).call();
                })
        );

        console.log(references);
        return {
            references
        };

    }
    PopupAddress(text) {
        return (
            <Popup content={text}
                trigger={<Button content={truncateMiddle(text, 5, 5, '....')} />} />
        )
    }
    renderRow() {


        return this.props.references.map((link, index) => {
            return (
                <Table.Row>
                    <Table.Cell>{index}</Table.Cell>
                    <Table.Cell>{link.TimeStamp}</Table.Cell>
                    {/* <Table.Cell>
                        {this.PopupAddress(link.DataHash)}
                    </Table.Cell> */}
                    <Table.Cell>
                        <Link route={`/records/read/${link.DataHash}`}>Read</Link>
                    </Table.Cell>
                </Table.Row>
            )
        });

    }
    render() {
        return (
            <Layout>
                <Grid>
                    <Grid.Column width={12}>
                        <Grid.Row>
                            <Table>
                                <Table.Header>
                                    <Table.Row>
                                        <Table.HeaderCell>ID</Table.HeaderCell>
                                        <Table.HeaderCell>Time</Table.HeaderCell>
                                        <Table.HeaderCell>Reference</Table.HeaderCell>
                                        <Table.HeaderCell>Read</Table.HeaderCell>
                                    </Table.Row>
                                </Table.Header>
                                <Table.Body>{this.renderRow()}</Table.Body>
                            </Table>
                            {/* <Link route={`/patients/${this.props.address}/requests`}>
                                <a>
                                    <Button content="view Requests" className="ui green button" />
                                </a>
                            </Link> */}
                        </Grid.Row>
                    </Grid.Column>
                </Grid>
            </Layout>
        );
    }
}
export default patientShow;
